jQuery(document).ready(function(){
  	jQuery(".btn-toggle").click(function(){
    	if(jQuery(this).hasClass('active'))
    	{
    		jQuery(this).siblings('.hidden-field-text').val(0);
    	}
    	else
    	{
    		jQuery(this).siblings('.hidden-field-text').val(1);
    	}
  	});

  	jQuery(".sm-submit").click(function(e){
        jQuery('.display-message').slideUp();
        jQuery('.display-message').html('');
    	e.preventDefault();    	
    	var frm = jQuery('#social_api_settings');
    	jQuery.ajax({
            type: frm.attr('method'),
            url:my_ajax_object.ajax_url,
            data: frm.serialize(),
            success: function (data) {
                if(data!='')
                {
                	jQuery('.display-message').slideDown();
	                var html_genrate = '';
	                html_genrate += '<div class="dis-message"><p>'+data+'</p></div>';
	                jQuery('.display-message').html(html_genrate);
                    setTimeout(function(){ 
                        jQuery('.display-message').slideUp();
                    },5000);
                }
            }
        });
	});
	// Pankaj
	// save display setting option
    jQuery(".sm-display-settings-submit").click(function(e){
        jQuery('.display-message').slideUp();
        jQuery('.display-message').html('');
        e.preventDefault();     
        var frm = jQuery('#social_display_settings');
        jQuery.ajax({
            type: frm.attr('method'),
            url:my_ajax_object.ajax_url,
            data: frm.serialize(),
            success: function (data) {
                if(data!='')
                {
                    jQuery('.display-message').slideDown();
                    var html_genrate = '';
                    html_genrate += '<div class="dis-message"><p>'+data+'</p></div>';
                    jQuery('.display-message').html(html_genrate);
                    setTimeout(function(){ 
                        jQuery('.display-message').slideUp();
                    },5000);
                    
                }
            }
        });
	});
	//Pankaj
});

